package IF.java;

public class ForEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("The integer datatype");
		int a[]= {10,9,22,34,4};
		for(int i:a)
		{
			System.out.println(i);
		}
		System.out.println("The character datatype");
		char ch[]= {'K','S','R','A','Z'};
		for(char c:ch)
		{
			System.out.println(c);
		}
		
	}

}
