package datatype;

public class Implicittypecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte S=10;
		int i=S;
		System.out.println(i);
		float f=25.5f;
		double d=f;
		System.out.println(d);
		char ch='A';
		int i2=ch;
		System.out.println(i2);
		char var1='\u00A7';
		int i3=var1;
		System.out.println(i3);
		
		

	}

}
